.container {
  width: 90%;
  max-width: 1400px;
  margin: 20px auto;
  /* Enable Flex layout, 3 columns by default */
  display: flex;
  gap: 20px;
}

.sidebar-left {
  width: 20%;
  background-color: #f5f5f5;
  padding: 15px;
}

.main-content {
  width: 60%;
  padding: 15px;
}

.sidebar-right {
  width: 20%;
  background-color: #f5f5f5;
  padding: 15px;
}

/* Tablet (768-1200px): 2 columns (main content + right sidebar, hide left sidebar) */
@media (max-width: 1200px) {
  .container {
    /* Adjust to 2 columns */
  }
  .sidebar-left {
    /* Hide left sidebar */
    display: none;
  }
  .main-content {
    width: 70%;
  }
  .sidebar-right {
    width: 30%;
  }
}

/* Mobile (<768px): 1 column, hide some navigation links */
@media (max-width: 768px) {
  .container {
    /* Adjust to 1 column */
    flex-direction: column;
  }
  .main-content, .sidebar-right {
    width: 100%;
  }
  .nav-links a:nth-child(4), .nav-links a:nth-child(5) {
    /* Hide 4th and 5th navigation links */
    display: none;
  }
}

